import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { setToken, decodeToken } from '../services/auth';
import { createUser } from '../services/userService';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [regUsername, setRegUsername] = useState('');
  const [regPassword, setRegPassword] = useState('');
  const [regRole, setRegRole] = useState('EMPLOYEE');
  const [regEmail, setRegEmail] = useState('');
  const [regSuccess, setRegSuccess] = useState(null);
  const [regErrors, setRegErrors] = useState({});
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setError(null);
    let errors = {};
    if (!regUsername.trim()) errors.username = 'Username is required.';
    if (!regPassword.trim()) errors.password = 'Password is required.';
    setRegErrors(errors);
    try {
      const res = await api.post('/api/v1/auth/login', { username, password });
      const token = res.data?.token || res.data?.accessToken || res.data?.jwt;
      if (!token) {
        setError('Login succeeded but no token was returned');
        return;
      }
      setToken(token, false);

      const payload = decodeToken();
      const role = (payload && (payload.role || payload.roles)) || null;
      const finalRole = Array.isArray(role) ? role[0] : role;
      if (/ADMIN/i.test(finalRole)) {
        navigate('/admin');
      } else {
        navigate('/employee');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setRegSuccess(null);
    setError(null);
    let errors = {};
    if (!regUsername.trim()) errors.username = 'Username is required.';
    if (!regEmail.trim()) errors.email = 'Email is required.';
    if (!regPassword.trim()) errors.password = 'Password is required.';
    if (!regRole.trim()) errors.role = 'Role is required.';
    setRegErrors(errors);
    if (Object.keys(errors).length > 0) return;
    try {
      await createUser({ username: regUsername, password: regPassword, role: regRole, email: regEmail });
      setRegSuccess('Registration successful! You can now login.');
      setShowRegister(false);
      setRegUsername('');
      setRegPassword('');
      setRegRole('EMPLOYEE');
      setRegEmail('');
      setRegErrors({});
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div
      className="container-fluid min-vh-100 d-flex justify-content-center align-items-center"
      style={{ background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)" }}
    >
      <div className="row w-100 justify-content-center">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow-lg border-0 rounded-4 p-4">
            <div className="card-body">
              <h3 className="text-center mb-4 fw-bold text-primary">
                {showRegister ? 'Create an Account' : 'Welcome Back'}
              </h3>

              {error && <div className="alert alert-danger">{error}</div>}
              {regSuccess && <div className="alert alert-success">{regSuccess}</div>}

              {/* Forms */}
              {showRegister ? (
                <form onSubmit={handleRegister} noValidate>
                  <div className="form-floating mb-3">
                    <input
                      className="form-control"
                      placeholder="Username"
                      value={regUsername}
                      onChange={(e) => setRegUsername(e.target.value)}
                      required
                    />
                    <label>Username</label>
                    {regErrors.username && <div className="text-danger small">{regErrors.username}</div>}
                  </div>
                  <div className="form-floating mb-3">
                    <input
                      type="password"
                      className="form-control"
                      placeholder="Password"
                      value={regPassword}
                      onChange={(e) => setRegPassword(e.target.value)}
                      required
                    />
                    <label>Password</label>
                    {regErrors.password && <div className="text-danger small">{regErrors.password}</div>}
                  </div>
                  <div className="form-floating mb-3">
                    <input
                      type="email"
                      className="form-control"
                      placeholder="Email"
                      value={regEmail}
                      onChange={(e) => setRegEmail(e.target.value)}
                      required
                    />
                    <label>Email</label>
                    {regErrors.email && <div className="text-danger small">{regErrors.email}</div>}
                  </div>
                  <div className="mb-3">
                    <label className="form-label fw-semibold">Role</label>
                    <select
                      className="form-select"
                      value={regRole}
                      onChange={(e) => setRegRole(e.target.value)}
                      required
                    >
                      <option value="EMPLOYEE">Employee</option>
                      <option value="ADMIN">Admin</option>
                    </select>
                  </div>
                  <button className="btn btn-success w-100 py-2 mb-2" type="submit">
                    Register
                  </button>
                  <button
                    className="btn btn-outline-secondary w-100 py-2"
                    type="button"
                    onClick={() => setShowRegister(false)}
                  >
                    Back to Login
                  </button>
                </form>
              ) : (
                <form onSubmit={submit} noValidate>
                  <div className="form-floating mb-3">
                    <input
                      className="form-control"
                      placeholder="Username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                    <label>Username</label>
                    {regErrors.username && <div className="text-danger small">{regErrors.username}</div>}
                  </div>
                  <div className="form-floating mb-3">
                    <input
                      type="password"
                      className="form-control"
                      placeholder="Password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                    <label>Password</label>
                    {regErrors.password && <div className="text-danger small">{regErrors.password}</div>}
                  </div>
                  <button className="btn btn-primary w-100 py-2 mb-3" type="submit">
                    Login
                  </button>
                  <button
                    className="btn btn-outline-light w-100 py-2"
                    type="button"
                    onClick={() => setShowRegister(true)}
                  >
                    Register
                  </button>
                </form>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
